import { ITheme, ThemeEnum } from "../interfaces/styled";
import { DefaultTheme } from "styled-components";

export const baseTheme: ITheme = {
  colors: {
    bg: "#18191a",
    font: "#19191B",
  },
};

export const lightTheme: DefaultTheme = {
  ...baseTheme,
  type: ThemeEnum.light,

  colors: {
    ...baseTheme.colors,
    bg: "#E5E4E8",
    font: "#19191B",
  },
};
